import React, { useState,useEffect } from 'react'; //useeffect for local storage
import ReactDOM from 'react-dom';
import '../src/index.css' // linking css file
import {v4 as uuid} from 'uuid'; // for making unique ids for todo list items

const App = () => { 
  const [category,setCategory] = useState('default');
  const [todo,setTodo] = useState('');
  const [date,setDate] = useState('1950-01-01');
  const [todos,setTodos] = useState([]);
  const [showtodos,savetodos] = useState(todos);
  const [search,setSearch] = useState('');
  const [filter,setFilter] = useState('');

  // storing data in local storage
  useEffect(() => {
    const items = JSON.parse(localStorage.getItem('items'));
    if(items){
      setTodos(items);
    }
  }, []);

  useEffect(() => {                                              // normal list
    localStorage.setItem('items', JSON.stringify(todos));
    savetodos(todos);
  }, [todos]);

  useEffect(() => {                                              // search list
    savetodos(todos.filter(t => t.text.includes(search)));
  }, [search]);

  useEffect(() => {                                              // filter list
    console.log(filter);
    console.log(showtodos);
    savetodos(todos.filter(t => t.filter===filter));
    if(filter==="all") savetodos(todos);
  }, [filter]);

  // Adding content to the list
  const addTodo = () => {
    const id = uuid();
    setTodos([...todos,{id:id,text:todo,status:false,category:category,date:date,filter:category}]);
  //setTodo("");
    localStorage.setItem("todos",JSON.stringify([...todos], {id:id,text:todo,status:false,type:category,time:date}))
  }

  // For  deleting a particular content
  const deleteTodo = (id) => {
    setTodos(todos.filter((t) => t.id !== id));
    localStorage.setItem("todos", JSON.stringify(todos.filter((t) => t.id !== id)))
  };

  // For adding checkbox which will have a strike through effect
  const markasdone = (id) => {
    setTodos(
      todos.map(t => {
        if(t.id==id) t.status = !t.status;
        return t;
      })
    )
  }

  const getcategory = () => {
    let temparr=[];
    { todos.map(todo => (
      temparr.push(todo.type)
    ))}
    setCategory(temparr)
  }

  const changestatus = () => {
    setResult(todos.filter(todo => {
      let flag=true;
      if(searchtodo.length>0) {
        if(!todo.text.includes(searchtodo)) {
          flag=false
        }
      }
    }))
  }

  return(
    <main>
      <div className='container'>
        <h1>To-Do List</h1>
        <div className="search-and-filter">

          <input
          value={search}
          className='search'
          onChange={(e) => setSearch(e.target.value)}
          placeholder='Search Here'
          type="text" />
          <div className="filters">
            <span>Filter By :-</span>
            <input type="date" />
            <select name="category" id="category" onChange={(e)=>setFilter(e.target.value)}>
            <option value="all">All Category</option>
            <option value="default">Default</option>
            <option value="food">Food</option>
            <option value="travel">Travel</option>
            <option value="fitness">Fitness</option>
          </select>

          </div>
        </div>
        <div className='add-container'>
        <input
        placeholder='Enter To-Do here'
        value = {todo}
        onChange={(e) => setTodo(e.target.value)}
        type="text" />
        <div className='category-container'>
          <input type="date" name="" id="" onChange={(e)=>setDate(e.target.value)}/>
          <select name="category" id="category" onChange={(e)=>setCategory(e.target.value)}>
            <option value="default">Default</option>
            <option value="food">Food</option>
            <option value="travel">Travel</option>
            <option value="fitness">Fitness</option>
          </select>
        </div>

          <button className='add-button' onClick={addTodo}>ADD</button>
        </div>
      <div className='todos-container'>
        {
          showtodos.map((todo) => {
            return <div className='todo-item'>
                     <input className='check' type="checkbox" onChange={() => {markasdone(todo.id)}} />
                     <div className='todo-details'>
                      <span>{todo.status===true?<s>{todo.text}</s>:todo.text}</span>
                      <span className='category'>{todo.status===true?<s>{todo.category}</s>:todo.category}</span>
                      <span className='date'>{todo.status===true?<s>{todo.date}</s>:todo.date}</span>
                     </div>
                     {<button className='delete-button' onClick={() => deleteTodo(todo.id)}>Delete</button>}
                   </div>
          })
        }
      </div>
      </div>
    </main>
  )
}

ReactDOM.render(<App />,document.getElementById('root'));
