import React, { useState,useEffect } from 'react'; //useeffect for local storage
import ReactDOM from 'react-dom';
import '../src/index.css' // linking css file
import {v4 as uuid} from 'uuid'; // for making unique ids for todo list items

const App = () => { 
    const [todo, setTodo] = useState("");
    const [category, setCategory] = useState("");
    const [date, setDate] = useState("");  
    const [todos, setTodos] = useState([]);
    // const [allCategory, setAllCategory] = useState([]);
    const [searchfield, setSearchfield] = useState("");
    const [allCategories, setallcategories] = useState(new Set());
    const [searchtodo, setSearchtodo] = useState("");
    const [date1, setDate1] = useState("");
    const [date2, setDate2] = useState("");
    const [searchcat, setSearchcat] = useState("");
    const [result,setResult] = useState([]);


  useEffect(() => {
    let arr = localStorage.getItem("todos");
    // alert(arr);
    if(arr){
        setTodos(JSON.parse(arr));
    }else{
        setTodos([]);
    }
  },[])

  useEffect(()=>{
      getallCategory();
  },[todos])

  // Adding content to the list
  const addTodo = () => {
    const id = uuid();
    setTodos([...todos,{id:id,text:todo,status:false,time:date,type:category}]);
  //setTodo("");
    localStorage.setItem("todos",JSON.stringify([...todos], {id:id,text:todo,status:false,time:date}))
  }

  // For  deleting a particular content
  const deleteTodo = (id) => {
    setTodos(todos.filter((t) => t.id !== id));
    localStorage.setItem("todos", JSON.stringify(todos.filter((t) => t.id !== id)))
  };

  // For adding checkbox which will have a strike through effect
  const markasdone = (id) => {
    setTodos(
      todos.map((t) => {
        if(t.id==id) t.status = !t.status;
        return t;
      })
    )
  }

  const getallCategory = () => {
    let temparr=[];
    { todos.map(todo => (
      temparr.push(todo.type)
    ))}
    setallcategories(temparr)
  }

  const handleChange = () => {
    setResult(todos.filter(todo => {
      let flag=true;
      if(searchtodo.length>0) {
        if(!todo.text.includes(searchtodo)) {
          flag=false;
        }
      }

      if(searchcat.length>0) {
        if(todo.type!==searchcat){
          flag=false;
        }
      }

      if(date1.length>0){
        if(((new Date(todo.time)) < (new Date(date1)))){
          flag=false;
        }
      }

      if(date2.length>0){
        if(((new Date(todo.time)) > (new Date(date2)))){
          flag=false;
        }
      }
      return flag;
    }))
  }

  return(
    // <main>
    //   <div className='container'>
    //     <h1>To-Do List</h1>
    //     <div className="search-and-filter">

    //       <input
    //       value={search}
    //       className='search'
    //       onChange={(e) => setSearch(e.target.value)}
    //       placeholder='Search Here'
    //       type="text" />
    //       <div className="filters">
    //         <span>Filter By :-</span>
    //         <input type="date" />
    //         <select name="category" id="category" onChange={(e)=>setFilter(e.target.value)}>
    //         <option value="all">All Category</option>
    //         <option value="default">Default</option>
    //         <option value="food">Food</option>
    //         <option value="travel">Travel</option>
    //         <option value="fitness">Fitness</option>
    //       </select>

    //       </div>
    //     </div>
    //     <div className='add-container'>
    //     <input
    //     placeholder='Enter To-Do here'
    //     value = {todo}
    //     onChange={(e) => setTodo(e.target.value)}
    //     type="text" />
    //     <div className='category-container'>
    //       <input type="date" name="" id="" onChange={(e)=>setDate(e.target.value)}/>
    //       <select name="category" id="category" onChange={(e)=>setCategory(e.target.value)}>
    //         <option value="default">Default</option>
    //         <option value="food">Food</option>
    //         <option value="travel">Travel</option>
    //         <option value="fitness">Fitness</option>
    //       </select>
    //     </div>

    //       <button className='add-button' onClick={addTodo}>ADD</button>
    //     </div>
    //   <div className='todos-container'>
    //     {
    //       showtodos.map((todo) => {
    //         return <div className='todo-item'>
    //                  <input className='check' type="checkbox" onChange={() => {markasdone(todo.id)}} />
    //                  <div className='todo-details'>
    //                   <span>{todo.status===true?<s>{todo.text}</s>:todo.text}</span>
    //                   <span className='category'>{todo.status===true?<s>{todo.category}</s>:todo.category}</span>
    //                   <span className='date'>{todo.status===true?<s>{todo.date}</s>:todo.date}</span>
    //                  </div>
    //                  {<button className='delete-button' onClick={() => deleteTodo(todo.id)}>Delete</button>}
    //                </div>
    //       })
    //     }
    //   </div>
    //   </div>
    // </main>

    <>
    <div>
      <h1> Todo list</h1>
    </div>
    <div>
      <input value={todo}
      onChange = {(e) => setTodo(e.target.value)} type = "text" placeholder="Add your text here">
      </input>
      <input value={date}
      onChange = {(e) => setDate(e.target.value)} type = "date">
      </input>
      <input value = {category} onChange = {(e) => setCategory(e.target.value)} type="text" placeholder="add category here"/>
      <button onClick={addTodo}>submit</button>
    </div>
    <div>
      <ol>
        {todos.map((todo) => {
          return (
            <li key={todo.id}>
              <div>
                <button type="checkbox" onClick={() => {markasdone(todo.id)}}/>
                {todo.status===true ? <s>{todo.text}</s> : todo.text}
                <button onClick={() => deleteTodo(todo.id)}>delete</button>
                <p>{todo.time}</p>
                <p>{todo.type}</p>
              </div>
            </li>
          );
        })}
      </ol>
    </div>

    <div>
                <>
                    <div>
                        <span>Start Date</span>
                        <span>End Date</span>                    
                    </div>
                    <div>
                        <input value={searchtodo} onChange={(e)=>setSearchtodo(e.target.value)} type ="text" placeholder="Search Task"></input>
                        <input onChange={(e)=>setDate1(e.target.value)} value = {date1} type="date"/>
                        <input onChange={(e)=>setDate2(e.target.value)} value ={date2} type="date"/>
                        <select onChange={(e)=>setSearchcat(e.target.value)}>
                            <option>all</option>
                            {(Array.from(allCategories)).map((singlecat) => (
                                <option>{singlecat}</option>
                            ))}</select>
                        <input value = {searchcat} type="text" placeholder="search by categroy"  onChange={(e)=>setSearchcat(e.target.value)}></input> 
                        <button onClick={handleChange} style={{marginLeft:"10px"}} >submit!</button>
                    </div>

                </>
                <ul>
                    {
                        (result.map(todo => {
                            return <li key={todo.id}>{todo.text}</li>
                        }))
                    }
                </ul>
           </div>
           {/* <p>{todo}</p> */}
           <div>
                    <div>
                        <button onClick={getallCategory}>Get All categories</button>
                    </div>
                    {(Array.from(allCategories)).map((singlecat) => (
                        <li>{singlecat}</li>
                    ))}
           </div>
        </>
    );
};
ReactDOM.render(<App />, document.getElementById("root"));
